import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import { Briefcase, GraduationCap } from "lucide-react";
import TrackVisibility from "react-on-screen";
import "animate.css";

export const ExperienceEducation = () => {
  const education = [
    {
      school: "Đại học Công nghệ Thông tin",
      major: "Kỹ thuật phần mềm",
      time: "2021 - 2025",
      icon: <GraduationCap size={24} />,
    },
  ];

  const experience = [
    {
      company: "Công ty XYZMove Logistic",
      position: "Thực tập sinh Front-end",
      time: "06/2024 - 09/2024",
      desc: "Tham gia thiết kế giao diện website quản lý vận chuyển, tích hợp API, tối ưu UX/UI.",
      icon: <Briefcase size={24} />,
    },
    {
      company: "Công ty LUIN",
      position: "Thiết kế Website bán hàng",
      time: "2023 - 2024",
      desc: "Thực hiện thiết kế website bán hàng responsive, hỗ trợ khách hàng triển khai nội dung.",
      icon: <Briefcase size={24} />,
    },
  ];

  return (
    <section className="experience-education" id="experience">
      <Container>
        <Row>
          <Col>
            <TrackVisibility>
              {({ isVisible }) => (
                <div className={isVisible ? "animate__animated animate__fadeInUp" : ""}>
                  <h2>Kinh nghiệm & Học vấn</h2>
                  <p>Hành trình học tập và làm việc được thể hiện qua timeline bên dưới.</p>
                  <div className="timeline">
                    <div className="timeline-column">
                      <h3><GraduationCap size={20} /> Học vấn</h3>
                      {education.map((edu, index) => (
                        <div key={index} className="timeline-item">
                          <div className="icon">{edu.icon}</div>
                          <div className="content">
                            <h4>{edu.school}</h4>
                            <h5>{edu.major}</h5>
                            <span>{edu.time}</span>
                          </div>
                        </div>
                      ))}
                    </div>
                    <div className="timeline-column">
                      <h3><Briefcase size={20} /> Kinh nghiệm</h3>
                      {experience.map((exp, index) => (
                        <div key={index} className="timeline-item">
                          <div className="icon">{exp.icon}</div>
                          <div className="content">
                            <h4>{exp.company}</h4>
                            <h5>{exp.position}</h5>
                            <span>{exp.time}</span>
                            <p>{exp.desc}</p>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
    </section>
  );
};
