import { Col, Row } from "react-bootstrap";

export const BlogSection = () => {
  const blogs = [
    {
      id: 1,
      title: "Cách tối ưu tốc độ website bán hàng với React",
      desc: "Tìm hiểu các kỹ thuật lazy loading, code splitting và caching giúp website của bạn tải nhanh hơn và thân thiện với người dùng.",
      img: "https://via.placeholder.com/400x220",
      link: "#"
    },
    {
      id: 2,
      title: "Thiết kế giao diện thân thiện cho người dùng di động",
      desc: "Responsive design giúp website hiển thị tốt trên mọi thiết bị. Bài viết chia sẻ các nguyên tắc UI/UX cơ bản.",
      img: "https://via.placeholder.com/400x220",
      link: "#"
    }
  ];

  return (
    <Col lg={12}>
      <div className="newsletter-bx blog-section-box wow fadeInUp">
        <Row>
          <Col lg={12}>
            <h3 style={{ color: "#121212", marginBottom: "40px" }}>
              Bài viết mới nhất
            </h3>
          </Col>

          {blogs.map((item) => (
            <Col key={item.id} md={6} className="mb-4">
              <div className="blog-card">
                <img src={item.img} alt={item.title} className="blog-img" />
                <div className="blog-text">
                  <h5>{item.title}</h5>
                  <p>{item.desc}</p>
                  <a href={item.link} className="blog-btn">
                    Đọc thêm
                  </a>
                </div>
              </div>
            </Col>
          ))}
        </Row>
      </div>
    </Col>
  );
};
