import { useState } from "react";
import { Col, Row, Button } from "react-bootstrap";

export const BlogSection = () => {
  // ✅ Dữ liệu mẫu
  const posts = [
    {
      id: 1,
      title: "Hiểu về React Hooks và cách áp dụng",
      date: "2025-10-20",
      content:
        "React Hooks giúp bạn quản lý state và vòng đời component dễ dàng hơn mà không cần class. Bài viết này sẽ hướng dẫn chi tiết cách sử dụng useState, useEffect và custom hooks.",
      tags: ["React", "Frontend"],
    },
    {
      id: 2,
      title: "Tối ưu hiệu năng website với Next.js",
      date: "2025-10-18",
      content:
        "Next.js mang lại khả năng SSR, SEO tốt hơn và tốc độ tải nhanh hơn cho ứng dụng web. Cùng tìm hiểu các kỹ thuật tối ưu hóa trong bài viết này.",
      tags: ["Next.js", "Performance"],
    },
    {
      id: 3,
      title: "REST API và GraphQL khác nhau như thế nào?",
      date: "2025-10-15",
      content:
        "Cả REST và GraphQL đều được sử dụng phổ biến trong việc xây dựng backend API. Tuy nhiên, mỗi phương pháp có ưu - nhược điểm riêng. Bài viết giúp bạn chọn giải pháp phù hợp.",
      tags: ["Backend", "API"],
    },
  ];

  // ✅ Lọc bài viết theo tag
  const [selectedTag, setSelectedTag] = useState("Tất cả");

  const allTags = ["Tất cả", ...new Set(posts.flatMap((post) => post.tags))];
  const filteredPosts =
    selectedTag === "Tất cả"
      ? posts
      : posts.filter((post) => post.tags.includes(selectedTag));

  return (
    <section className="blog-section" id="blog">
      <div className="container">
        <h2 className="text-center mb-4">Blog - Chia sẻ kiến thức & Công nghệ</h2>

        {/* Bộ lọc tag */}
        <div className="tag-filter text-center mb-4">
          {allTags.map((tag) => (
            <Button
              key={tag}
              variant={selectedTag === tag ? "primary" : "outline-light"}
              onClick={() => setSelectedTag(tag)}
              className="mx-2"
            >
              {tag}
            </Button>
          ))}
        </div>

        {/* Hiển thị bài viết */}
        <Row>
          {filteredPosts.map((post) => (
            <Col key={post.id} md={4} className="mb-4">
              <div className="blog-card p-4 h-100">
                <h4 className="blog-title">{post.title}</h4>
                <small className="text-muted">{post.date}</small>
                <p className="mt-3">{post.content}</p>
                <div className="mt-auto">
                  {post.tags.map((tag, index) => (
                    <span key={index} className="badge bg-gradient me-2">
                      {tag}
                    </span>
                  ))}
                </div>
              </div>
            </Col>
          ))}
        </Row>
      </div>
    </section>
  );
};
